mapper.select:
select *
from t_article


        and id = #{article.id}


        and title LIKE CONCAT('%',#{article.title  },'%')


        and content LIKE CONCAT('%',#{article.content  },'%')


        and status LIKE CONCAT('%',#{article.status  },'%')


        and score = #{article.score}


        and create_time = #{article.createTime}


        and update_time = #{article.updateTime}


        and create_by LIKE CONCAT('%',#{article.createBy  },'%')


        and update_by LIKE CONCAT('%',#{article.updateBy  },'%')


order by id desc
