mapper.select:
select *
from  t_gonggao


        and id = #{gonggao.id}


        and   title LIKE CONCAT('%',#{gonggao.title  },'%')


        and content LIKE CONCAT('%',#{gonggao.content  },'%')


        and create_time = #{gonggao.createTime}


        and update_time = #{gonggao.updateTime}


        and create_by LIKE CONCAT('%',#{gonggao.createBy  },'%')


        and update_by LIKE CONCAT('%',#{gonggao.updateBy  },'%')


order by id desc
