mapper.select:
        select *
        from  t_jilu


                and id = #{jilu.id}


                and user_id = #{jilu.userId}


                and detail LIKE CONCAT('%',#{jilu.detail  },'%')


                and update_num = #{jilu.updateNum}


                and sum = #{jilu.sum}


                and create_time = #{jilu.createTime}


                and update_time = #{jilu.updateTime}


                and create_by LIKE CONCAT('%',#{jilu.createBy  },'%')


                and update_by LIKE CONCAT('%',#{jilu.updateBy  },'%')


order by id desc
