mapper.select:
select *
from  t_prize


        and id = #{prize.id}


        and name LIKE CONCAT('%',#{prize.name  },'%')


        and img LIKE CONCAT('%',#{prize.img  },'%')


        and detials LIKE CONCAT('%',#{prize.detials  },'%')


        and score LIKE CONCAT('%',#{prize.score  },'%')


        and num = #{prize.num}


        and create_time = #{prize.createTime}


        and update_time = #{prize.updateTime}


        and create_by LIKE CONCAT('%',#{prize.createBy  },'%')


        and update_by LIKE CONCAT('%',#{prize.updateBy  },'%')


order by id desc
